DROP DATABASE IF EXISTS project;

CREATE DATABASE project CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

use project;

CREATE TABLE IF NOT EXISTS `users` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(100),
  `email` VARCHAR(100) NOT NULL UNIQUE,
  `password` VARCHAR(100) NOT NULL,
  `created_at` date DEFAULT now(),
  `updated_at` date DEFAULT now(),
  `level` tinyint DEFAULT '0',
  PRIMARY KEY `pk_ad`(`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `category` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` NVARCHAR(100),
  `status` tinyint DEFAULT '1',
  PRIMARY KEY `pk_cate`(`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `product` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` NVARCHAR(100),
  `image` VARCHAR(100),
  `price` float,
  `sale_price` float,
  `description` text,
  `category_id` int NOT NULL,
  PRIMARY KEY `pk_prd`(`id`),
  FOREIGN KEY (`category_id`) REFERENCES `category` (`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `customer` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` NVARCHAR(100),
  `email` VARCHAR(100) NOT NULL UNIQUE,
  `phone` VARCHAR(100) NOT NULL UNIQUE,
  `password` VARCHAR(100) NOT NULL,
  `address` VARCHAR(100) NULL,
  `status` tinyint DEFAULT '1',
  PRIMARY KEY `pk_ctm`(`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `orders` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` NVARCHAR(100),
  `email` VARCHAR(100) NOT NULL UNIQUE,
  `phone` VARCHAR(100) NOT NULL UNIQUE,
  `customer_id` INT NOT NULL,
  `address` NVARCHAR(100) NULL,
  `status` tinyint DEFAULT '0',
  `date` date DEFAULT NOW(),
  `order_note`  NVARCHAR(100) NULL,
  PRIMARY KEY `pk_ord`(`id`),
  FOREIGN KEY (`customer_id`) REFERENCES `customer` (`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `blog` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `title` NVARCHAR(100),
  `induction` text,
  `image_title` VARCHAR(100),
  `customer_id` INT NOT NULL DEFAULT '1',
  `content` text ,
  `image` NVARCHAR(100),
  `date` date DEFAULT NOW(),
  `view`  int DEFAULT '1',
  PRIMARY KEY `pk_blog`(`id`),
  FOREIGN KEY (`customer_id`) REFERENCES `customer` (`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `order_detail` (
  `order_id` INT NOT NULL,
  `product_id` INT NOT NULL,
  `quantity` INT NOT NULL,
  `price` float NOT NULL,
  PRIMARY KEY `pk_ord_d`(`order_id`,`product_id`),
  FOREIGN KEY (`product_id`) REFERENCES `product` (`id`),
  FOREIGN KEY (`order_id`) REFERENCES `orders` (`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `banner` (
  `order_id` INT NOT NULL,
  `product_id` INT NOT NULL,
  `quantity` INT NOT NULL,
  `price` float NOT NULL,
  PRIMARY KEY `pk_ord_d`(`order_id`,`product_id`),
  FOREIGN KEY (`product_id`) REFERENCES `product` (`id`),
  FOREIGN KEY (`order_id`) REFERENCES `orders` (`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `comment` (
  `order_id` INT NOT NULL,
  `product_id` INT NOT NULL,
  `quantity` INT NOT NULL,
  `price` float NOT NULL,
  PRIMARY KEY `pk_ord_d`(`order_id`,`product_id`),
  FOREIGN KEY (`product_id`) REFERENCES `product` (`id`),
  FOREIGN KEY (`order_id`) REFERENCES `orders` (`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `reating` (
  `id` INT NOT NULL,
  `product_id` INT NOT NULL,
  `quantity` INT NOT NULL,
  `price` float NOT NULL,
  PRIMARY KEY `pk_ord_d`(`order_id`,`product_id`),
  FOREIGN KEY (`product_id`) REFERENCES `product` (`id`),
  FOREIGN KEY (`order_id`) REFERENCES `orders` (`id`)
) ENGINE = InnoDB;


